class ArrayWrapper {
	final int[] x;

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + Arrays.hashCode(x);
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		ArrayWrapper other = (ArrayWrapper) obj;
		if (!Arrays.equals(x, other.x))
			return false;
		return true;
	}

	public ArrayWrapper(int[] x) {
		this.x = x;
	}

	int[] getX() {
		return x;
	}

	public int getSum() {
		int sum = 0;
		for (int i : x) {
			sum += i;
		}
		return sum;
	}

	public List<Integer> toList() {
		List<Integer> l = new ArrayList<Integer>();
		for (Integer i : x) {
			l.add(i);
		}
		return l;
	}
}
